import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = function () {
  /*const [enterUserState, setenterUserState] = useState({
    enterTitle: "",
    enterAmount: "",
    enterDate: "",
  });
*/
  const [enterTitle, setEnterTitle] = useState("");
  const [amount, SetAmount] = useState("");
  const [date, setDate] = useState("");
  
  const titleChangehandler = (event) => {
  
      setEnterTitle( event.target.value)
    
  };

  const amountChange = (event) => {
   SetAmount(event.target.value);
  };

  const dateChange = (event) => {
    setDate(event.target.value);
  };

  const SubmitHandler=(event)=>
  {
    event.preventDefault();

    const expenseData={
        title:enterTitle,
        amountt:amount,
        datee:new Date(date)
    };
    console.log(expenseData);
  };


  return (
    <form onSubmit={SubmitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>{titleChangehandler}</label>
          <input type="text" />
        </div>

        <div className="new-expense__control">
          <label>Title</label>
          <input type="number" min="0.01" step="0.01" onChange={enterTitle}/>
        </div>

        <div className="new-expense__control">
          <label></label>
          <input type="date" min="2019-01-01" max="2026-01-10" />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="Submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
